import React from "react";
import { useLocation } from "react-router-dom";
import QRCode from "react-qr-code";

function UserProfile() {
  const location = useLocation();
  const { userEmail } = location.state;

  const [eventName, setEventName] = useState("");
  const [eventDate, setEventDate] = useState();
  const [eventTime, setEventTime] = useState();
  const [eventVenue, setEventVenue] = useState();

  void function handleSubmit(e) {
    e.preventDefault();
    setEventName(e.target[0].value);
    setEventDate(e.target[1].value);
    setEventTime(e.target[2].value);
    setEventVenue(e.target[3].value);
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title text-center mb-4">User Profile</h5>
              <p className="text-center">Welcome, {userEmail}!</p>
              <input type="text" value={userEmail} readOnly />
              <input type="text" value={userEmail} readOnly />
              <input type="text" value={userEmail} readOnly />
              <button className="btn btn-primary w-100 mt-4">
                Create QRCode
              </button>
            </div>
            <div className="card-footer text-center mt-4 mb-4 p-4 bg-light rounded">
              <QRCode value={userEmail} />
              <p>Scan this QR code to access your profile</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default UserProfile;
